import React, { useState, useEffect } from 'react';
import './App.css';

const API_URL = 'http://localhost:3003';

const perfilPadrao = {
  imagem: '',
  nome: '',
  idade: '',
  rua: '',
  bairro: '',
  estado: '',
  biografia: ''
};

function App() {
  const [perfil, setPerfil] = useState(perfilPadrao);
  const [editando, setEditando] = useState(false);
  const [form, setForm] = useState(perfilPadrao);
  const [imagemPreview, setImagemPreview] = useState('');
  const [imagemArquivo, setImagemArquivo] = useState(null);
  const [carregando, setCarregando] = useState(true);

  // Buscar dados do usuÃ¡rio ao carregar
  useEffect(() => {
    fetch(`${API_URL}/usuario`)
      .then(res => res.json())
      .then(data => {
        setPerfil(data);
        setForm(data);
        setImagemPreview(data.imagem || '');
        setCarregando(false);
      });
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImagemChange = e => {
    const file = e.target.files[0];
    if (file) {
      setImagemArquivo(file);
      setImagemPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    if (imagemArquivo) {
      formData.append('imagem', imagemArquivo);
    }
    formData.append('nome', form.nome);
    formData.append('idade', form.idade);
    formData.append('rua', form.rua);
    formData.append('bairro', form.bairro);
    formData.append('estado', form.estado);
    formData.append('biografia', form.biografia);
    const resp = await fetch(`${API_URL}/usuario`, {
      method: 'POST',
      body: formData
    });
    const data = await resp.json();
    setPerfil(data);
    setForm(data);
    setImagemPreview(data.imagem || '');
    setImagemArquivo(null);
    setEditando(false);
  };

  if (carregando) {
    return <div className="flex items-center justify-center min-h-screen">Carregando...</div>;
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-200 to-purple-300 p-4">
      <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-8">
        {!editando ? (
          <div className="text-center">
            <img src={perfil.imagem ? `${API_URL}${perfil.imagem}` : ''} alt="Perfil" className="w-28 h-28 rounded-full mx-auto mb-4 object-cover" />
            <h2 className="text-2xl font-bold mb-1">{perfil.nome}</h2>
            <p className="text-gray-500 mb-2">{perfil.idade} anos</p>
            <p className="text-gray-600 mb-2">{perfil.rua}, {perfil.bairro} - {perfil.estado}</p>
            <p className="text-gray-700 mb-4">{perfil.biografia}</p>
            <button onClick={() => setEditando(true)} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition">Editar Perfil</button>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-left text-sm font-medium text-gray-700">Foto de Perfil</label>
              <input type="file" accept="image/*" onChange={handleImagemChange} className="mt-1 block w-full" />
              {imagemPreview && (
                <img src={imagemPreview} alt="PrÃ©via" className="w-20 h-20 rounded-full mt-2 mx-auto object-cover" />
              )}
            </div>
            <div>
              <label className="block text-left text-sm font-medium text-gray-700">Nome Completo</label>
              <input type="text" name="nome" value={form.nome} onChange={handleChange} className="mt-1 block w-full border rounded px-3 py-2" />
            </div>
            <div>
              <label className="block text-left text-sm font-medium text-gray-700">Idade</label>
              <input type="number" name="idade" value={form.idade} onChange={handleChange} className="mt-1 block w-full border rounded px-3 py-2" />
            </div>
            <div>
              <label className="block text-left text-sm font-medium text-gray-700">Rua</label>
              <input type="text" name="rua" value={form.rua} onChange={handleChange} className="mt-1 block w-full border rounded px-3 py-2" />
            </div>
            <div>
              <label className="block text-left text-sm font-medium text-gray-700">Bairro</label>
              <input type="text" name="bairro" value={form.bairro} onChange={handleChange} className="mt-1 block w-full border rounded px-3 py-2" />
            </div>
            <div>
              <label className="block text-left text-sm font-medium text-gray-700">Estado</label>
              <input type="text" name="estado" value={form.estado} onChange={handleChange} className="mt-1 block w-full border rounded px-3 py-2" />
            </div>
            <div>
              <label className="block text-left text-sm font-medium text-gray-700">Biografia</label>
              <textarea name="biografia" value={form.biografia} onChange={handleChange} className="mt-1 block w-full border rounded px-3 py-2" />
            </div>
            <div className="flex gap-2">
              <button type="submit" className="flex-1 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition">Salvar</button>
              <button type="button" onClick={() => setEditando(false)} className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400 transition">Cancelar</button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default App;
