import React, { useState, useEffect } from 'react';

// URL da API do backend
const API_URL = 'http://localhost:3001';

// Estrutura padrão do perfil do usuário
const perfilPadrao = {
  imagem: '',
  nome: '',
  idade: '',
  rua: '',
  bairro: '',
  estado: '',
  biografia: ''
};

export default function PaginaPerfil() {
  // Estados para armazenar os dados do perfil, formulário, imagem, etc.
  const [perfil, setPerfil] = useState(perfilPadrao); // Dados exibidos
  const [editando, setEditando] = useState(false); // Se está no modo de edição
  const [form, setForm] = useState(perfilPadrao); // Dados do formulário de edição
  const [imagemPreview, setImagemPreview] = useState(''); // Pré-visualização da imagem
  const [imagemArquivo, setImagemArquivo] = useState(null); // Arquivo da imagem selecionada
  const [carregando, setCarregando] = useState(true); // Estado de carregamento

  // Ao carregar a página, busca os dados do usuário no backend
  useEffect(() => {
    fetch(`${API_URL}/usuario`)
      .then(res => res.json())
      .then(data => {
        setPerfil(data);
        setForm(data);
        setImagemPreview(data.imagem || '');
        setCarregando(false);
      });
  }, []);

  // Atualiza o estado do formulário conforme o usuário digita
  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Quando o usuário seleciona uma nova imagem
  const handleImagemChange = e => {
    const file = e.target.files[0];
    if (file) {
      setImagemArquivo(file);
      setImagemPreview(URL.createObjectURL(file));
    }
  };

  // Ao enviar o formulário de edição
  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    // Se o usuário escolheu uma nova imagem, adiciona ao FormData
    if (imagemArquivo) {
      formData.append('imagem', imagemArquivo);
    }
    // Adiciona os demais campos ao FormData
    formData.append('nome', form.nome);
    formData.append('idade', form.idade);
    formData.append('rua', form.rua);
    formData.append('bairro', form.bairro);
    formData.append('estado', form.estado);
    formData.append('biografia', form.biografia);
    // Envia os dados para o backend
    const resp = await fetch(`${API_URL}/usuario`, {
      method: 'POST',
      body: formData
    });
    const data = await resp.json();
    // Atualiza o perfil exibido com os dados salvos
    setPerfil(data);
    setForm(data);
    setImagemPreview(data.imagem || '');
    setImagemArquivo(null);
    setEditando(false);
  };

  // Enquanto está carregando os dados, exibe uma mensagem
  if (carregando) {
    return <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-gray-100 to-white">Carregando informações do seu perfil...</div>;
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-100 to-white p-4">
      <div className="w-full max-w-md bg-white/90 backdrop-blur rounded-2xl shadow-xl p-8 border border-gray-200 relative">
        {/* Avatar do usuário */}
        <div className="absolute -top-12 left-1/2 -translate-x-1/2">
          <div className="bg-white p-1 rounded-full shadow border-2 border-blue-200">
            <img
              src={(!editando ? (perfil.imagem ? `${API_URL}${perfil.imagem}` : '') : imagemPreview) || 'https://ui-avatars.com/api/?name=Usuário&background=E0E7EF&color=3B82F6'}
              alt="Foto do perfil"
              className="w-28 h-28 rounded-full object-cover border-4 border-white shadow bg-white"
              style={{ animation: 'none' }}
            />
          </div>
        </div>
        <div className="mt-16">
          {/* Exibição dos dados do perfil */}
          {!editando ? (
            <div className="text-center">
              <h2 className="text-2xl font-bold text-gray-800 mb-1 tracking-tight">
                {perfil.nome || <span className='text-gray-400'>Seu nome</span>}
              </h2>
              <p className="text-base text-gray-500 mb-2">
                {perfil.idade ? perfil.idade + ' anos' : <span className='text-gray-300'>Idade</span>}
              </p>
              <p className="text-gray-500 mb-2">
                {perfil.rua || <span className='text-gray-300'>Rua</span>}, {perfil.bairro || <span className='text-gray-300'>Bairro</span>} - {perfil.estado || <span className='text-gray-300'>Estado</span>}
              </p>
              <p className="text-gray-600 mb-4 italic">
                {perfil.biografia || <span className='text-gray-300'>Sua biografia</span>}
              </p>
              <button
                onClick={() => setEditando(true)}
                className="px-6 py-2 bg-blue-500 text-white rounded-full font-semibold shadow hover:bg-blue-600 transition-all duration-200"
              >
                Editar Perfil
              </button>
            </div>
          ) : (
            // Formulário de edição do perfil
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="flex flex-col items-center">
                <label className="block text-sm font-medium text-gray-700">Foto de Perfil</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImagemChange}
                  className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                />
                {imagemPreview && (
                  <img
                    src={imagemPreview}
                    alt="Prévia da foto"
                    className="w-20 h-20 rounded-full mt-2 mx-auto object-cover border-2 border-blue-200 shadow"
                  />
                )}
              </div>
              <div>
                <label className="block text-left text-sm font-medium text-gray-700">Nome Completo</label>
                <input
                  type="text"
                  name="nome"
                  value={form.nome}
                  onChange={handleChange}
                  className="mt-1 block w-full border-none rounded-xl px-3 py-2 bg-gray-100 focus:ring-2 focus:ring-blue-300 focus:outline-none shadow-sm"
                  placeholder="Digite seu nome completo"
                />
              </div>
              <div className="flex gap-2">
                <div className="flex-1">
                  <label className="block text-left text-sm font-medium text-gray-700">Idade</label>
                  <input
                    type="number"
                    name="idade"
                    value={form.idade}
                    onChange={handleChange}
                    className="mt-1 block w-full border-none rounded-xl px-3 py-2 bg-gray-100 focus:ring-2 focus:ring-blue-300 focus:outline-none shadow-sm"
                    placeholder="Sua idade"
                  />
                </div>
                <div className="flex-1">
                  <label className="block text-left text-sm font-medium text-gray-700">Estado</label>
                  <input
                    type="text"
                    name="estado"
                    value={form.estado}
                    onChange={handleChange}
                    className="mt-1 block w-full border-none rounded-xl px-3 py-2 bg-gray-100 focus:ring-2 focus:ring-blue-300 focus:outline-none shadow-sm"
                    placeholder="Seu estado"
                  />
                </div>
              </div>
              <div className="flex gap-2">
                <div className="flex-1">
                  <label className="block text-left text-sm font-medium text-gray-700">Rua</label>
                  <input
                    type="text"
                    name="rua"
                    value={form.rua}
                    onChange={handleChange}
                    className="mt-1 block w-full border-none rounded-xl px-3 py-2 bg-gray-100 focus:ring-2 focus:ring-blue-300 focus:outline-none shadow-sm"
                    placeholder="Sua rua"
                  />
                </div>
                <div className="flex-1">
                  <label className="block text-left text-sm font-medium text-gray-700">Bairro</label>
                  <input
                    type="text"
                    name="bairro"
                    value={form.bairro}
                    onChange={handleChange}
                    className="mt-1 block w-full border-none rounded-xl px-3 py-2 bg-gray-100 focus:ring-2 focus:ring-blue-300 focus:outline-none shadow-sm"
                    placeholder="Seu bairro"
                  />
                </div>
              </div>
              <div>
                <label className="block text-left text-sm font-medium text-gray-700">Biografia</label>
                <textarea
                  name="biografia"
                  value={form.biografia}
                  onChange={handleChange}
                  className="mt-1 block w-full border-none rounded-xl px-3 py-2 bg-gray-100 focus:ring-2 focus:ring-blue-300 focus:outline-none shadow-sm resize-none"
                  placeholder="Conte um pouco sobre você..."
                />
              </div>
              <div className="flex gap-2 mt-4">
                <button
                  type="submit"
                  className="flex-1 px-4 py-2 bg-blue-500 text-white rounded-full font-semibold shadow hover:bg-blue-600 transition-all duration-200"
                >
                  Salvar
                </button>
                <button
                  type="button"
                  onClick={() => setEditando(false)}
                  className="flex-1 px-4 py-2 bg-gray-200 text-gray-700 rounded-full font-semibold shadow hover:bg-gray-300 transition-all duration-200"
                >
                  Cancelar
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}
